    logType: "configuration",
    destinationType: "s3",
    s3Bucket: tailscaleLogs.id,
    s3Region: "us-west-2",
    s3AuthenticationType: "rolearn",
    s3RoleArn: tailscaleLogsWriter.arn,
    s3ExternalId: prod.externalId,
});
// Example configuration for an S3-compatible logstreaming endpoint
